import React, {useRef, useState} from "react";
import axios from 'axios'
import {ChangeEvent} from "react";

type FormState = {
    email: string;
    name: string;
    message: string;
}

const Contact = () => {

    const formID = "HdX8sO1w";
    const formSparkURL = 'https://submit-form.com/' + formID

    const initialFormState = {
        email: '',
        name: '',
        message: '',
    }

    const [formState, setFormState] = useState<FormState>(initialFormState);

    const submitForm = async (event: FormEvent) => {
        event.preventDefault();
        await postSubmission();
    }

    const postSubmission = async () => {
        const payload = {
            message: 'Test formspark message number 283'
        }

        try {
            const result = await axios.post(formSparkURL, payload);
        } catch(error){
            console.log(error)
        }
    }

    const updateFormControl = (event: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { id, value } = event.target;
        const formKey = id;
        const updateFormState = {...formState};
        updateFormState
    }

    return (

        <div className="contact-div">

            <form onSubmit={submitForm}>

                <div className="formSection">
                    <label htmlFor={"name'"}>Name</label>
                    <input onChange={updateFormControl} type={"text"} id={"name"} value={formState.name}/>
                </div>

                <button className={"contact-button"}>Submit</button>

            </form>

        </div>

    );
};

export default Contact
