import React, { useState } from "react";
import ReactDOM from "react-dom";
import { useFormspark } from "@formspark/use-formspark";

const FORMSPARK_FORM_ID = "HdX8sO1w";

const Application = () => {
    const [submit, submitting] = useFormspark({
        formId: FORMSPARK_FORM_ID,
    });

    const [message, setMessage] = useState("");

    const onSubmit = async (e) => {
        e.preventDefault();
        await submit({ message });
        alert("Form submitted");
    };

    return (

        <div id={"contact-div"}>
            <a className={"anchor"} id={"contact"}></a>
            <h3 className={"m-3"}>Contact</h3>
            <form id={"contact-form"}>
                <p>Name:</p>
                <input id={"contact-name-box"} type={"text"} placeholder={"Your name here"}></input>
                <p>Email:</p>
                <input id={"contact-email-box"} type={"text"} placeholder={"Your email here"}></input>
                <p>Message:</p>
                <textarea value={message} onChange={(e) => setMessage(e.target.value)} placeholder={"Your message here"}></textarea><br/>
                <button type="submit" disabled={submitting}>
                    Send
                </button>
                <p>WIP!!! Don't try anything funny</p>
            </form>
        </div>

    );
};

export default Application


